    def __diffsoundex(self, query, name, value=4):
        soundex = fuzzy.Soundex(value)
        a = soundex(name)
        b = soundex(query)
        if a[0] == b[0]:
            return abs(int(a[1:]) - int(b[1:]))
        else :
            return abs(int(a[1:]) - int(b[1:])) + 250

